import React, { useState, useEffect } from 'react';
import {
  Typography,
  Box,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Chip
} from '@mui/material';

// Dans une application réelle, nous importerions un service API
// import { getSports } from '../services/api';

const SportsPage = () => {
  const [loading, setLoading] = useState(true);
  const [sports, setSports] = useState([]);

  useEffect(() => {
    const fetchSports = async () => {
      try {
        // Dans une app réelle : const data = await getSports();
        // Simuler un appel API
        await new Promise(resolve => setTimeout(resolve, 1000));

        // Données simulées
        const mockSports = [
          {
            sport_id: 1,
            nom: "Football",
            categorie: "Collectif",
            nombre_joueurs: 11,
            description: "Le football se joue avec un ballon entre deux équipes de 11 joueurs"
          },
          {
            sport_id: 2,
            nom: "Basketball",
            categorie: "Collectif",
            nombre_joueurs: 5,
            description: "Le basketball est un sport où deux équipes de cinq joueurs s'affrontent pour marquer des paniers"
          },
          {
            sport_id: 3,
            nom: "Tennis",
            categorie: "Individuel",
            nombre_joueurs: 2,
            description: "Le tennis est un sport de raquette qui oppose soit deux joueurs, soit deux équipes de deux joueurs"
          },
          {
            sport_id: 4,
            nom: "Rugby",
            categorie: "Collectif",
            nombre_joueurs: 15,
            description: "Le rugby est un sport collectif de contact qui se joue avec un ballon ovale"
          },
          {
            sport_id: 5,
            nom: "Volleyball",
            categorie: "Collectif",
            nombre_joueurs: 6,
            description: "Le volleyball est un sport où deux équipes s'affrontent avec un ballon sur un terrain séparé par un filet"
          }
        ];

        setSports(mockSports);
        setLoading(false);
      } catch (error) {
        console.error("Erreur lors du chargement des sports:", error);
        setLoading(false);
      }
    };

    fetchSports();
  }, []);

  const getCategorieColor = (categorie) => {
    switch (categorie.toLowerCase()) {
      case 'collectif':
        return 'primary';
      case 'individuel':
        return 'secondary';
      default:
        return 'default';
    }
  };

  return (
    <Box>
      <Typography variant="h4" gutterBottom>
        Sports
      </Typography>

      {loading ? (
        <Typography>Chargement des sports...</Typography>
      ) : (
        <TableContainer component={Paper}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>ID</TableCell>
                <TableCell>Nom</TableCell>
                <TableCell>Catégorie</TableCell>
                <TableCell>Nombre de joueurs</TableCell>
                <TableCell>Description</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {sports.map((sport) => (
                <TableRow key={sport.sport_id}>
                  <TableCell>{sport.sport_id}</TableCell>
                  <TableCell>{sport.nom}</TableCell>
                  <TableCell>
                    <Chip 
                      label={sport.categorie} 
                      color={getCategorieColor(sport.categorie)} 
                      size="small" 
                    />
                  </TableCell>
                  <TableCell>{sport.nombre_joueurs}</TableCell>
                  <TableCell>{sport.description}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      )}
    </Box>
  );
};

export default SportsPage;
